// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.20;
import "@openzeppelin/contracts/access/AccessControl.sol";

contract AccessManager is AccessControl {
    bytes32 public constant VALIDATOR_ROLE = keccak256("VALIDATOR_ROLE");
    bytes32 public constant AGENT_ROLE = keccak256("AGENT_ROLE");

    constructor(address admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
    }

    /**
     * Use this modifier to manage the fonction access more granularly
     * @param _functionSig The signature of the function to be called
     */
    modifier functionRoleOrAdmin(bytes32 _functionSig) {
        require(
            hasRole(DEFAULT_ADMIN_ROLE, _msgSender()) || hasRole(_functionSig, _msgSender()),
            "[AccessManagement] Only the Admin or function granted user can interact"
        );
        _;
    }

    /**
     * @dev Add a validator to the contract
     */
    function addValidator(address _validator) public functionRoleOrAdmin(this.addValidator.selector) {
        _grantRole(VALIDATOR_ROLE, _validator);
    }

    /**
     * @dev Remove a validator from the contract
     */
    function removeValidator(address _validator) public functionRoleOrAdmin(this.removeValidator.selector) {
        _revokeRole(VALIDATOR_ROLE, _validator);
    }

    /**
     * @dev Check if an address has the admin role
     */
    function hasAdminRole(address _address) public view returns (bool) {
        return hasRole(DEFAULT_ADMIN_ROLE, _address);
    }

    /**
     * @dev Check if an address has the validator role
     * @param _address The address to check
     */
    function hasValidatorRole(address _address) public view returns (bool) {
        return hasRole(VALIDATOR_ROLE, _address);
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 _interfaceId) public view virtual override(AccessControl) returns (bool) {
        return super.supportsInterface(_interfaceId);
    }
}

contract AccessManagerBase is Context {
    AccessManager accessManager;

    constructor(address _accessManager) {
        accessManager = AccessManager(_accessManager);
    }

    /**
     * Use this modifier to restrict to admin only
     */
    modifier onlyAdmin() {
        require(
            accessManager.hasRole(accessManager.DEFAULT_ADMIN_ROLE(), _msgSender()),
            "[AccessManagement] Only the admin can interact"
        );
        _;
    }

    /**
     * Use this modifier to restrict to validator only
     */
    modifier onlyValidator() {
        require(
            accessManager.hasRole(accessManager.VALIDATOR_ROLE(), _msgSender()),
            "[AccessManagement] Only the validator can interact"
        );
        _;
    }

    /**
     * Use this modifier to restrict to validator only
     */
    modifier onlyAgent() {
        require(
            accessManager.hasRole(accessManager.AGENT_ROLE(), _msgSender()),
            "[AccessManagement] Only the agent can interact"
        );
        _;
    }

    /**
     * Use this modifier to manage the fonction access more granularly
     */
    modifier onlyRole(bytes32 _role) {
        require(accessManager.hasRole(_role, _msgSender()), "[AccessManager] Only the validator can interact");
        _;
    }

    /**
     * Use this modifier to manage the fonction access more granularly
     * @param _functionSig The signature of the function to be called
     */
    modifier functionRoleOrAdmin(bytes32 _functionSig) {
        require(
            hasRole(accessManager.DEFAULT_ADMIN_ROLE(), _msgSender()) || hasRole(_functionSig, _msgSender()),
            "[AccessManagement] Only the Admin or function granted user can interact"
        );
        _;
    }

    function hasAdminRole() public view returns (bool) {
        return accessManager.hasAdminRole(_msgSender());
    }

    function hasValidatorRole() public view returns (bool) {
        return accessManager.hasValidatorRole(_msgSender());
    }

    function hasRole(bytes32 _role, address _address) public view returns (bool) {
        return accessManager.hasRole(_role, _address);
    }
}

contracts/DeedNFT.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.20;
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "./AccessManager.sol";

contract DeedNFT is ERC721, ERC721URIStorage, AccessManagerBase {
    struct DeedInfo {
        AssetType assetType;
        bool isValidated;
    }

    uint256 public nextDeedId;
    mapping(uint256 => DeedInfo) private deedInfoMap;

    enum AssetType {
        Land,
        Vehicle,
        Estate,
        CommercialEquipment
    }

    event DeedNFTMinted(uint256 deedId, DeedInfo deedInfo, address minter);
    event DeedNFTBurned(uint256 deedId);
    event DeedNFTAssetValidationSet(uint256 deedId, bool isValid);
    event DeedNFTIpfsDetailsSet(uint256 deedId, string newIpfsDetailsHash);
    event DeedNFTAssetTypeSet(uint256 deedId, AssetType newAssetType);

    constructor(address _accessManager) ERC721("DeedNFT", "DEED") ERC721URIStorage() AccessManagerBase(_accessManager) {
        nextDeedId = 1;
    }

    modifier deedExists(uint256 _deedId) {
        require(
            _ownerOf(_deedId) != address(0),
            string.concat("[DeedNFT] Deed does not exist with id ", Strings.toString(_deedId))
        );
        _;
    }

    modifier onlyOwner(uint256 _deedId) {
        require(
            _msgSender() == _ownerOf(_deedId),
            string.concat("[DeedNFT] Must be owner of the Deed with id ", Strings.toString(_deedId))
        );
        _;
    }

    function mintAsset(
        address _owner,
        string memory _ipfsDetailsHash,
        AssetType _assetType
    ) public onlyValidator returns (uint256) {
        _mint(_owner, nextDeedId);

        DeedInfo storage deedInfo = deedInfoMap[nextDeedId];
        deedInfo.assetType = _assetType;
        _setTokenURI(nextDeedId, _ipfsDetailsHash);
        deedInfo.isValidated = true;
        emit DeedNFTMinted(nextDeedId, deedInfo, _msgSender());
        nextDeedId = nextDeedId + 1;
        return nextDeedId;
    }

    function burn(uint256 _deedId) public onlyOwner(_deedId) {
        _burn(_deedId);
        emit DeedNFTBurned(_deedId);
    }

    function setAssetValidation(uint256 _deedId, bool _isValid) public onlyValidator {
        require(_ownerOf(_deedId) != _msgSender(), "[DeedNFT] Owner cannot validate their own asset");
        _setAssetValidation(_deedId, _isValid);
    }

    function setIpfsDetailsHash(
        uint256 _deedId,
        string memory _ipfsDetailsHash
    ) public virtual deedExists(_deedId) onlyOwner(_deedId) {
        _setTokenURI(_deedId, _ipfsDetailsHash);
        _setAssetValidation(_deedId, false);
        emit DeedNFTIpfsDetailsSet(_deedId, _ipfsDetailsHash);
    }

    function setAssetType(uint256 _deedId, AssetType _assetType) public deedExists(_deedId) onlyOwner(_deedId) {
        DeedInfo storage deedInfo = deedInfoMap[_deedId];
        deedInfo.assetType = _assetType;
        _setAssetValidation(_deedId, false);
        emit DeedNFTAssetTypeSet(_deedId, _assetType);
    }

    function getDeedInfo(uint256 _deedId) public view deedExists(_deedId) returns (DeedInfo memory) {
        return deedInfoMap[_deedId];
    }

    function canSubdivide(uint256 _deedId) external view returns (bool) {
        AssetType assetType = getDeedInfo(_deedId).assetType;
        return assetType == AssetType.Land || assetType == AssetType.Estate;
    }

    function supportsInterface(
        bytes4 _interfaceId
    ) public view virtual override(ERC721, ERC721URIStorage) returns (bool) {
        return super.supportsInterface(_interfaceId);
    }

    function tokenURI(uint256 _deedId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        return super.tokenURI(_deedId);
    }

    function _setAssetValidation(uint256 _deedId, bool _isValid) internal {
        DeedInfo storage deedInfo = deedInfoMap[_deedId];
        deedInfo.isValidated = _isValid;
        emit DeedNFTAssetValidationSet(_deedId, _isValid);
    }
}

contracts/FundsManager.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.20;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./AccessManager.sol";

contract FundsManager is Context, AccessManagerBase {
    event FundsStored(uint256 id, IERC20 token, uint256 amount, address sender, address caller, uint256 newBalance);
    event FundsWithdrawn(
        uint256 id,
        IERC20 token,
        uint256 amount,
        address destination,
        address caller,
        uint256 newBalance
    );

    // AccountOwner     ->    AccountId   ->  TokenAddress -> Amount
    mapping(address => mapping(uint256 => mapping(address => uint256))) public accountsMapping;

    constructor(address _accessManager) AccessManagerBase(_accessManager) {}

    /**
     * Get the balance of an account
     * @param _id AccountId
     * @param _token TokenAddress
     * @return Balance
     */
    function balanceOf(uint256 _id, IERC20 _token) external view returns (uint256) {
        return accountsMapping[_msgSender()][_id][address(_token)];
    }

    /**
     * Pull tokens from sender and store them in the contract
     * @param _id AccountId
     * @param _token TokenAddress
     * @param _amount Amount
     * @param _sender Witch address to pull tokens from
     */
    function store(uint256 _id, IERC20 _token, uint256 _amount, address _sender) external {
        require(
            _token.allowance(_sender, address(this)) >= _amount,
            string.concat(
                "[Funds Manager] Not enough allowance for account ",
                Strings.toString(_id),
                " and amount ",
                Strings.toString(_amount)
            )
        );

        _token.transferFrom(_sender, address(this), _amount);
        accountsMapping[_msgSender()][_id][address(_token)] += _amount;

        emit FundsStored(
            _id,
            _token,
            _amount,
            _sender,
            _msgSender(),
            accountsMapping[_msgSender()][_id][address(_token)]
        );
    }

    /**
     * Withdraw tokens from the contract and send them to the recipient
     * @param _id AccountId
     * @param _token TokenAddress
     * @param _amount Amount
     * @param _recipient Witch address to send tokens to
     */
    function withdraw(uint256 _id, IERC20 _token, uint256 _amount, address _recipient) external {
        require(
            accountsMapping[_msgSender()][_id][address(_token)] >= _amount,
            string.concat(
                "[Funds Manager] Not enough funds for account ",
                Strings.toString(_id),
                " and amount ",
                Strings.toString(_amount)
            )
        );

        accountsMapping[_msgSender()][_id][address(_token)] -= _amount;
        _token.transfer(_recipient, _amount);

        emit FundsWithdrawn(
            _id,
            _token,
            _amount,
            _recipient,
            _msgSender(),
            accountsMapping[_msgSender()][_id][address(_token)]
        );
    }
}

contracts/LeaseAgreement.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./DeedNFT.sol"; // Import the IDeedNFT interface
import "./LeaseNFT.sol";
import "./SubdivisionNFT.sol"; // Import the ISubdivisionNFT interface
import "./FundsManager.sol";
import "./AccessManager.sol";

interface ILeaseNFT {
    function mint(address leaseOwner, uint256 leaseId) external;

    function burn(uint256 leaseId) external;
}

contract LeaseAgreement is ReentrancyGuard, AccessManagerBase {
    // CONSTANTS
    uint256 private constant MONTH = 30.5 days;
    uint256 private constant MAX_NB_MONTH_FOR_DEPOSIT = 3;

    struct LeaseDates {
        uint256 startDate;
        uint256 endDate;
        uint256 rentDueDate;
        uint256 distributableDate;
    }

    struct Deposit {
        uint256 amount;
        bool paid;
    }

    struct Lease {
        address[] lesseeList;
        uint256 rentAmount;
        Deposit securityDeposit;
        uint256 latePaymentFee;
        uint32 gracePeriod;
        LeaseDates dates;
        uint256 extensionCount;
        uint256 deedId;
        address manager;
        uint8 managerPercentage;
        uint256 unclaimedRentAmount;
        bool isArchived;
    }

    struct RentPaymentInfo {
        uint256 rentAmount;
        uint256 totalBalance;
        uint32 unpaidMonths;
        uint256 rentDueDate;
    }

    modifier onlyLessor(uint256 _leaseId) {
        require(_msgSender() == leaseNFT.ownerOf(_leaseId), "[Lease Agreement] Sender must be Lessor");
        _;
    }
    modifier onlyActive(uint256 _leaseId) {
        require(
            block.timestamp < leases[_leaseId].dates.endDate && block.timestamp > leases[_leaseId].dates.startDate,
            "[Lease Agreement] The contract has been archived."
        );
        _;
    }
    mapping(uint256 => Lease) public leases;
    uint256 public leaseCounter;
    LeaseNFT public leaseNFT;
    IERC20 public paymentToken;
    DeedNFT public deedNFT;
    SubdivisionNFT public subdivisionNFT;
    FundsManager public fundsManager;

    event LeaseFundsManagerSet(address fundsManager);
    event LeasePaymentTokenSet(address paymentToken);
    event LeaseCreated(uint256 leaseId, Lease lease);
    event LeaseTerminated(uint256 leaseId);
    event LeasePaymentMade(uint256 leaseId, uint256 amount, uint256 unclaimedRentAmount);
    event LeaseRentDistributed(uint256 leaseId, uint256 lessorAmount, uint256 managerAmount, uint256 distributableDate);
    event LeaseManagerSet(uint256 leaseId, address manager, uint256 percentage);
    event LeaseManagerUnset(uint256 leaseId);
    event LeaseDueDateChanged(uint256 leaseId, uint256 newDueDate);
    event LeaseDepositSubmited(uint256 leaseId, uint256 amount);
    event LeaseExtended(uint256 leaseId, uint256 endDate, uint256 rentAmount, uint256 extensionCount);

    constructor(
        address _leaseNFT,
        address _paymentToken,
        address _deedNFT,
        address _subdivisionNFT,
        address _fundsManager,
        address _accessManager
    ) AccessManagerBase(_accessManager) {
        require(_leaseNFT != address(0), "[Lease Agreement] Invalid LeaseNFT address");
        require(_paymentToken != address(0), "[Lease Agreement] Invalid token address");
        require(_deedNFT != address(0), "[Lease Agreement] Invalid DeedNFT address");
        require(_subdivisionNFT != address(0), "[Lease Agreement] Invalid SubdivisionNFT address");

        leaseNFT = LeaseNFT(_leaseNFT);
        deedNFT = DeedNFT(_deedNFT);
        subdivisionNFT = SubdivisionNFT(_subdivisionNFT);

        paymentToken = IERC20(_paymentToken);
        emit LeasePaymentTokenSet(_paymentToken);

        fundsManager = FundsManager(_fundsManager);
        emit LeaseFundsManagerSet(_fundsManager);

        leaseCounter = 0;
    }

    function setFundsManager(address _fundsManager) public functionRoleOrAdmin(this.setFundsManager.selector) {
        fundsManager = FundsManager(_fundsManager);
        emit LeaseFundsManagerSet(_fundsManager);
    }

    function getLesseeList(uint256 _leaseId) public view returns (address[] memory) {
        Lease storage lease = leases[_leaseId];
        return lease.lesseeList;
    }

    function setPaymentToken(address _paymentToken) public functionRoleOrAdmin(this.setPaymentToken.selector) {
        paymentToken = IERC20(_paymentToken);
        emit LeasePaymentTokenSet(_paymentToken);
    }

    function createLease(
        address[] memory _lesseeList,
        uint256 _startDate,
        uint256 _endDate,
        uint256 _rentAmount,
        uint256 _securityDepositAmount,
        uint256 _deedId,
        uint256 _latePaymentFee,
        uint32 _gracePeriod
    ) external {
        require(_startDate < _endDate, "[Lease Agreement] Invalid start and end dates");
        require(
            _endDate - _startDate > 1 * MONTH,
            "[Lease Agreement] End date and start date should be 1 month appart"
        );
        require(_gracePeriod >= 3);
        require(_latePaymentFee >= 0 && _latePaymentFee <= 15);
        bool isDeedOwner = _verifyDeedOwnership(_msgSender(), _deedId);
        bool isSubdivisionOwner = _verifySubdivisionOwnership(_msgSender(), _deedId);
        require(isDeedOwner || isSubdivisionOwner, "[Lease Agreement] Lessor must own the property NFT");

        uint256 leaseId = leaseCounter;
        leaseCounter++;
        Lease storage lease = leases[leaseId];
        lease.lesseeList = _lesseeList;
        lease.dates.startDate = _startDate;
        lease.dates.endDate = _endDate;
        lease.rentAmount = _rentAmount;
        lease.securityDeposit.amount = _securityDepositAmount;
        lease.dates.rentDueDate = _startDate + 1 * MONTH;
        lease.extensionCount = 0;
        lease.deedId = _deedId;
        lease.securityDeposit.paid = _securityDepositAmount == 0;
        lease.latePaymentFee = _latePaymentFee;
        lease.gracePeriod = _gracePeriod;
        lease.isArchived = false;
        leaseNFT.mint(_msgSender(), leaseId);
        lease.dates.distributableDate = lease.dates.rentDueDate;

        emit LeaseCreated(leaseId, lease);
    }

    function containsAddress(address[] memory _addresses, address _address) internal pure returns (bool) {
        bool contains = false;
        for (uint i = 0; i < _addresses.length; i++) {
            if (_addresses[i] == _address) {
                contains = true;
                break;
            }
        }
        return contains;
    }

    function transferLease(uint256 _leaseId, address[] memory _newLesseeList) public onlyLessor(_leaseId) {
        Lease storage lease = leases[_leaseId];
        lease.lesseeList = _newLesseeList;
    }

    function setManager(uint256 _leaseId, address _manager, uint8 _percentage) public onlyLessor(_leaseId) {
        Lease storage lease = leases[_leaseId];
        require(_percentage >= 0 && _percentage <= 100, "[Lease Agreement] Invalid Manager percentage");

        lease.manager = _manager;
        lease.managerPercentage = _percentage;

        emit LeaseManagerSet(_leaseId, _manager, _percentage);
    }

    function unsetManager(uint256 _leaseId) external {
        Lease storage lease = leases[_leaseId];
        require(
            _msgSender() == leaseNFT.ownerOf(_leaseId) || _msgSender() == lease.manager,
            "[Lease Agreement] Only the Lessor or the Manager can remove the Manager"
        );

        lease.manager = address(0);
        lease.managerPercentage = 0;

        emit LeaseManagerUnset(_leaseId);
    }

    function submitDeposit(uint256 _leaseId) external nonReentrant {
        Lease storage lease = leases[_leaseId];
        require(
            containsAddress(lease.lesseeList, _msgSender()),
            "[Lease Agreement] Only the Lessee can submit the deposit"
        );
        require(!lease.securityDeposit.paid, "[Lease Agreement] Security deposit already paid");

        fundsManager.store(_leaseId, paymentToken, lease.securityDeposit.amount, _msgSender());
        lease.securityDeposit.paid = true;

        emit LeaseDepositSubmited(_leaseId, lease.securityDeposit.amount);
    }

    function setDueDate(uint256 _leaseId, uint256 _newDueDate) public onlyLessor(_leaseId) {
        Lease storage lease = leases[_leaseId];
        require(
            _newDueDate >= (lease.dates.rentDueDate + 1 * MONTH),
            "[Lease Agreement] New rent due date must be at least a month after current one"
        );

        lease.dates.rentDueDate = _newDueDate;

        emit LeaseDueDateChanged(_leaseId, _newDueDate);
    }

    function payRent(uint256 _leaseId) external nonReentrant {
        Lease storage lease = leases[_leaseId];
        require(containsAddress(lease.lesseeList, _msgSender()), "[Lease Agreement] Only the Lessee can pay rent");
        require(lease.securityDeposit.paid, "[Lease Agreement] Security deposit must be paid first");
        require(
            block.timestamp >= lease.dates.startDate && block.timestamp <= lease.dates.endDate,
            "[Lease Agreement] Outside of lease duration"
        );

        RentPaymentInfo memory rentInfo = calculateRentPaymentInfo(_leaseId);
        lease.unclaimedRentAmount += rentInfo.totalBalance;
        lease.dates.rentDueDate += (rentInfo.unpaidMonths) * (1 * MONTH);
        fundsManager.store(_leaseId, paymentToken, rentInfo.totalBalance, _msgSender());

        emit LeaseDueDateChanged(_leaseId, lease.dates.rentDueDate);
        emit LeasePaymentMade(_leaseId, rentInfo.totalBalance, lease.unclaimedRentAmount);
    }

    function distributeRent(uint256 _leaseId) external nonReentrant {
        Lease storage lease = leases[_leaseId];
        require(
            _msgSender() == leaseNFT.ownerOf(_leaseId) || _msgSender() == lease.manager,
            "[Lease Agreement] Caller must be the Lessor or the Manager"
        );
        require(lease.unclaimedRentAmount > 0, "[Lease Agreement] No rent to distribute");
        uint256 nbMonthSinceLastDistribute = 0;
        require(
            block.timestamp > lease.dates.distributableDate,
            "[Lease Agreement] Rent can only be distributed past the distributable date"
        );

        nbMonthSinceLastDistribute = (block.timestamp - lease.dates.distributableDate) / (1 * MONTH) + 1;
        uint256 totalToClaim = lease.unclaimedRentAmount;
        uint256 managerAmount = 0;

        if (lease.manager != address(0)) {
            managerAmount = (totalToClaim * lease.managerPercentage) / 100;
            fundsManager.withdraw(_leaseId, paymentToken, uint32(managerAmount), lease.manager);
        }

        uint256 lessorAmount = totalToClaim - managerAmount;
        fundsManager.withdraw(_leaseId, paymentToken, uint32(lessorAmount), leaseNFT.ownerOf(_leaseId));
        lease.unclaimedRentAmount = 0;
        lease.dates.distributableDate += nbMonthSinceLastDistribute * (1 * MONTH);

        emit LeaseRentDistributed(_leaseId, uint32(lessorAmount), uint32(managerAmount), lease.dates.distributableDate);
    }

    function extendLease(uint256 _leaseId, uint256 _extensionPeriod) external onlyLessor(_leaseId) {
        Lease storage lease = leases[_leaseId];
        require(!lease.isArchived, "[Lease Agreement] Lease has been archived");
        require(
            block.timestamp >= lease.dates.endDate - 45 days, // TODO: Configurable
            "[Lease Agreement] Extension can only be requested in the last 45 days"
        );
        require(lease.extensionCount < 2, "[Lease Agreement] Maximum extensions reached");
        lease.dates.endDate += _extensionPeriod;
        lease.rentAmount += (lease.rentAmount * 3) / 100;
        lease.extensionCount++;

        emit LeaseExtended(_leaseId, lease.dates.endDate, lease.rentAmount, lease.extensionCount);
    }

    function withdrawDeposit(uint256 _leaseId) external nonReentrant {
        Lease storage lease = leases[_leaseId];
        require(
            lease.isArchived || block.timestamp > lease.dates.endDate,
            "[Lease Agreement] Cannot withdraw deposit if Lease still active"
        );
        require(containsAddress(lease.lesseeList, _msgSender()), "[Lease Agreement] Caller must be one of the lessees");
        RentPaymentInfo memory rentInfo = calculateRentPaymentInfo(_leaseId);
        bool shouldSendDepositToLessor = (rentInfo.unpaidMonths >= MAX_NB_MONTH_FOR_DEPOSIT);
        // Send security deposit to the sender of the lessee list
        if (!shouldSendDepositToLessor) {
            fundsManager.withdraw(_leaseId, paymentToken, lease.securityDeposit.amount, _msgSender());
        }
        lease.isArchived = true;
    }

    function burnLease(uint256 _leaseId) public {
        Lease storage lease = leases[_leaseId];
        require(
            _msgSender() == leaseNFT.ownerOf(_leaseId) || _msgSender() == lease.manager,
            "[Lease Agreement] Sender must be manager or lessor"
        );
        require(lease.isArchived, "[Lease Agreement] Lease has been archived");
        leaseNFT.burn(_leaseId);
    }

    function terminateLease(uint256 _leaseId) external nonReentrant onlyLessor(_leaseId) {
        Lease storage lease = leases[_leaseId];
        require(block.timestamp >= lease.dates.startDate, "[Lease Agreement] Lease has not started yet");
        RentPaymentInfo memory rentInfo = calculateRentPaymentInfo(_leaseId);
        bool shouldSendDepositToLessor = (rentInfo.unpaidMonths >= MAX_NB_MONTH_FOR_DEPOSIT);

        // Send security deposit to the lessor if needed
        if (shouldSendDepositToLessor) {
            fundsManager.withdraw(_leaseId, paymentToken, lease.securityDeposit.amount, leaseNFT.ownerOf(_leaseId));
        }
        lease.isArchived = true;
        emit LeaseTerminated(_leaseId);
    }

    function calculateRentPaymentInfo(uint256 _leaseId) public view returns (RentPaymentInfo memory rentInfo) {
        Lease storage lease = leases[_leaseId];
        rentInfo.rentAmount = lease.rentAmount;
        // sidenote: rent due date is incremented by 1 month each time the rent is paid.(function payRent)
        if (block.timestamp > lease.dates.rentDueDate + (lease.gracePeriod * 1 days)) {
            // Function to calculate rentAmountIncrease increase
            rentInfo.rentAmount += (lease.rentAmount * lease.latePaymentFee) / 100;
            rentInfo.unpaidMonths = uint32((block.timestamp - lease.dates.rentDueDate) / (1 * MONTH) + 1);
            rentInfo.totalBalance = (rentInfo.unpaidMonths) * rentInfo.rentAmount;
        } else {
            rentInfo.unpaidMonths = 1;
            rentInfo.totalBalance = rentInfo.rentAmount;
        }
        return rentInfo;
    }

    function _verifySubdivisionOwnership(address _owner, uint256 _subdivisionId) internal view returns (bool) {
        return subdivisionNFT.isOwnerOfSubdivision(_owner, _subdivisionId);
    }

    function _verifyDeedOwnership(address _owner, uint256 _deedId) internal view returns (bool) {
        try deedNFT.ownerOf(_deedId) returns (address owner) {
            return owner == _owner;
        } catch {
            return false;
        }
    }
}

contracts/LeaseNFT.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import "./AccessManager.sol";

contract LeaseNFT is ERC721, AccessManagerBase {
    address private leaseAgreementAddress;

    event LeaseNFTMinted(address leaseOwner, uint256 leaseId, address minter);
    event LeaseNFTBurned(uint256 leaseId, address burner);
    event LeaseNFTAgreementSet(uint256 leaseId, address burner);

    constructor(address _accessManager) ERC721("LeaseNFT", "LEASE") AccessManagerBase(_accessManager) {}

    function setLeaseAgreementAddress(address _leaseAgreementAddress) external onlyAdmin {
        leaseAgreementAddress = _leaseAgreementAddress;
    }

    function mint(address _to, uint256 _leaseId) external {
        require(
            hasAdminRole() || _msgSender() == leaseAgreementAddress,
            "[LeaseNFT] Only LeaseAgreement contract can mint the lease"
        );
        _mint(_to, _leaseId);
        emit LeaseNFTMinted(_to, _leaseId, _msgSender());
    }

    function exists(uint256 _leaseId) public view returns (bool) {
        return _ownerOf(_leaseId) != address(0);
    }

    function burn(uint256 _leaseId) external {
        require(
            hasAdminRole() || _msgSender() == leaseAgreementAddress,
            "[LeaseNFT] Only LeaseAgreement can burn the lease"
        );
        _burn(_leaseId);
        emit LeaseNFTBurned(_leaseId, _msgSender());
    }
}

contracts/SubdivisionNFT.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.20;
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./DeedNFT.sol";
import "./AccessManager.sol";

contract SubdivisionNFT is ERC1155, AccessManagerBase {
    struct SubdivisionInfo {
        bytes ipfsDetailsHash;
        address owner;
        uint256 parentDeed;
    }

    uint256 private _nextsubTokenID;
    mapping(uint256 => SubdivisionInfo) private subdivisionInfoMap;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    DeedNFT public deedNFT;

    event SubdivisionMinted(
        address subdivisionOwner,
        uint256 subdivisionId,
        uint256 deedId,
        bytes ipfsDetailsHash,
        address minter
    );
    event SubdivisionBurned(address account, uint256 subdivisionId, uint256 deedId, bytes ipfsDetailsHash);
    event SubdivisionInfoSet(uint256 tokenId, SubdivisionInfo info);

    constructor(address _deedNFT, address _accessManager) ERC1155("") AccessManagerBase(_accessManager) {
        require(_deedNFT != address(0), "[SubdivisionNFT] Invalid DeedNFT address");

        _nextsubTokenID = 1;
        deedNFT = DeedNFT(_deedNFT);
    }

    function mintSubdivision(SubdivisionInfo memory _info) public {
        require(
            _msgSender() == deedNFT.ownerOf(_info.parentDeed),
            "[SubdivisionNFT] Sender must be the owner of the parent deed"
        );
        require(deedNFT.canSubdivide(_info.parentDeed), "[SubdivisionNFT] Parent deed must be land or estate");

        _mint(_info.owner, _nextsubTokenID, 1, _info.ipfsDetailsHash);
        SubdivisionInfo storage subInfo = subdivisionInfoMap[_nextsubTokenID];
        subInfo.ipfsDetailsHash = _info.ipfsDetailsHash;
        subInfo.owner = _info.owner;
        subInfo.parentDeed = _info.parentDeed;

        emit SubdivisionMinted(_info.owner, _nextsubTokenID, _info.parentDeed, _info.ipfsDetailsHash, _msgSender());
        _nextsubTokenID = _nextsubTokenID + 1;
    }

    function batchMint(SubdivisionInfo[] memory _infos) public {
        for (uint i = 0; i < _infos.length; i++) {
            mintSubdivision(_infos[i]);
        }
    }

    function setInfo(uint256 _subTokenId, SubdivisionInfo memory _newInfo) public virtual {
        require(
            isOwnerOfSubdivision(_msgSender(), _subTokenId) == true,
            "[SubdivisionNFT] Sender Must be owner of subdivision to set info"
        );

        subdivisionInfoMap[_subTokenId] = _newInfo;

        emit SubdivisionInfoSet(_subTokenId, _newInfo);
    }

    function burnSubdivision(uint256 _subTokenId) public {
        require(
            isOwnerOfSubdivision(_msgSender(), _subTokenId) == true,
            "[SubdivisionNFT] Sender must be owner of the subdivision to burn it"
        );

        _burn(_msgSender(), _subTokenId, 1);

        emit SubdivisionBurned(
            _msgSender(),
            _subTokenId,
            subdivisionInfoMap[_subTokenId].parentDeed,
            subdivisionInfoMap[_subTokenId].ipfsDetailsHash
        );
    }

    function getParentDeed(uint256 _subTokenId) public view returns (uint256) {
        SubdivisionInfo storage info = subdivisionInfoMap[_subTokenId];
        return info.parentDeed;
    }

    function isOwnerOfSubdivision(address _owner, uint256 _subTokenId) public view returns (bool) {
        return balanceOf(_owner, _subTokenId) > 0;
    }

    function supportsInterface(bytes4 _interfaceId) public view virtual override(ERC1155) returns (bool) {
        return super.supportsInterface(_interfaceId);
    }
}
